func calculateFinalFloor(instructions string) int {
	currentFloor := 0

	for _, char := range instructions {
		if char == '(' {
			currentFloor++
		} else if char == ')' {
			currentFloor--
		}
	}
	return currentFloor
}

func findBasementEntryPosition(instructions string) int {
	currentFloor := 0
	for position, char := range instructions {
		if char == '(' {
			currentFloor++
		} else if char == ')' {
			currentFloor--
		}

		if currentFloor == -1 {
			return position + 1 // bcs positions start at 0
		}
	}
	return -1 // if the basement is never reached
}
