func calculateFinalFloor(instructions string) int {
	// Начальный этаж
	currentFloor := 0

	// Перебор всех символов в строке инструкций
	for _, char := range instructions {
		switch char {
		case '(':
			currentFloor++
		case ')':
			currentFloor--
		}
	}
	// Возвращение конечного этажа, на котором оказался Санта
	return currentFloor
}
